import React from 'react';
import { useForm } from 'react-hook-form';
export default
//render dynamic form with react hook form 
function App() {
  const { register, handleSubmit, watch, errors } = useForm();
  const tableData = {
    "modal": [
      {
        "section": "Beneficiary Name and Country",
        "order": 1,
        "components": [
          {
            "key": "name",
            "type": "textbox",
            "label": "Beneficiary name",
            "order": 1,
            "rule": {
              "required": true,
              "maxLength": 10
            }
          },
          {
            "key": "country",
            "type": "textbox",
            "label": "Beneficiary Country",
            "order": 2,
            "rule": {
              "required": true,
              "maxLength": 10
            }
          }
        ]
      },
      {
        "section": "Mode of Receiving Payment",
        "order": 2,
        "components": [
          {
            "key": "mode",
            "type": "radio",
            "label": "Mode of Payment",
            "options": [
              {
                "label": "Bank",
                "value": "Bank"
              },
              {
                "label": "other",
                "value": "other"
              }
            ],
            "order": 1,
            "rule": {
              "required": true
            }
          }
        ]
      },
      {
        "section": "Bank Account Details",
        "components": [
          {
            "key": "accountNum",
            "type": "textbox",
            "label": "Beneficiary Account Number",
            "order": 1,
            "rule": {
              "required": true,
              "maxLength": 10
            }
          },
          {
            "key": "bankRoutingCode",
            "type": "textbox",
            "label": "Beneficiary Bank Routing code",
            "order": 2,
            "rule": {
              "required": true,
              "maxLength": 10
            }
          },
          {
            "key": "address",
            "type": "textbox",
            "label": "Beneficiary Address",
            "order": 3
          }
        ]
      }
    ]
  };
  const renderForm = (tableData) => {
    return tableData.modal.map((section, index) => {
      return (
        <div key={index}>
          <h3>{section.section}</h3>
          {section.components.map((component, index) => {
            return (
              <div key={index}>
                <label>{component.label}</label>
                {component.type === "textbox" && <input name={component.key} ref={register(component.rule)} />}
                {component.type === "radio" && component.options.map((option, index) => {
                  return (
                    <div key={index}>
                      <input type="radio" name={component.key} value={option.value} ref={register(component.rule)} />
                      <label>{option.label}</label>
                    </div>
                  ) 
                })}
              </div>
            )
          })}
        </div>
      )
    })
  }
  return (
    <div className="App">
      <form onSubmit={handleSubmit(onSubmit)}>
        {renderForm(tableData)}
        <input type="submit" />
      </form>
    </div>
  );
}
