import React from 'react';
import { useForm } from 'react-hook-form';

//render form dynamically wih react hook form
export default function App() {
    const { register, handleSubmit, errors } = useForm();
    const onSubmit = data => console.log(data);
    const tableData = {
        "modal": [
          {
            "section": "Beneficiary Name and Country",
            "order": 1,
            "components": [
              {
                "key": "name",
                "type": "textbox",
                "label": "Beneficiary name",
                "order": 1,
                "rule": {
                  "required": true,
                  "maxLength": 10
                }
              },
              {
                "key": "country",
                "type": "textbox",
                "label": "Beneficiary Country",
                "order": 2,
                "rule": {
                  "required": true,
                  "maxLength": 10
                }
              }
            ]
          },
          {
            "section": "Mode of Receiving Payment",
            "order": 2,
            "components": [
              {
                "key": "mode",
                "type": "radio",
                "label": "Mode of Payment",
                "options": [
                  {
                    "label": "Bank",
                    "value": "Bank"
                  },
                  {
                    "label": "other",
                    "value": "other"
                  }
                ],
                "order": 1,
                "rule": {
                  "required": true
                }
              }
            ]
          },
          {
            "section": "Bank Account Details",
            "components": [
              {
                "key": "accountNum",
                "type": "textbox",
                "label": "Beneficiary Account Number",
                "order": 1,
                "rule": {
                  "required": true,
                  "maxLength": 10
                }
              },
              {
                "key": "bankRoutingCode",
                "type": "textbox",
                "label": "Beneficiary Bank Routing code",
                "order": 2,
                "rule": {
                  "required": true,
                  "maxLength": 10
                }
              },
              {
                "key": "address",
                "type": "textbox",
                "label": "Beneficiary Address",
                "order": 3
              }
            ]
          }
        ]
      };
    //validate form with react hook form
    const validateForm = (rule, value) => {

        if (rule.required && !value) {
            return 'This field is required';
        }
        if (rule.maxLength && value.length > rule.maxLength) {
            return 'This field is too long';
        }
        return true;
    }
    //render form dynamically with react hook form render filds with order
    const renderForm = () => {
        return tableData.modal.map((section, index) => {
            return (
                <div key={index}>
                    <h3>{section.section}</h3>
                    {section.components.map((component, index) => {
                        return (
                            <div key={index}>
                                <label>{component.label}</label>
                                {component.type === 'textbox' && <input type="text" name={component.key} ref={register({ validate: (value) => validateForm(component.rule, value) })} />}
                                {component.type === 'radio' && component.options.map((option, index) => {
                                    return (
                                        <div key={index}>
                                            <input type="radio" name={component.key} value={option.value} ref={register({ validate: (value) => validateForm(component.rule, value) })} />
                                            <label>{option.label}</label>
                                        </div>
                                    )
                                })}
                            </div>
                        )
                    })}
                </div>
            )
        })
    }
}
